INSERT INTO `experimental_description` VALUES ('23', '实验项目介绍', '<p><span style=\"color: #000000; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, Oxygen, Ubuntu, Cantarell, \'Fira Sans\', \'Droid Sans\', \'Helvetica Neue\', sans-serif; font-size: 30px; font-weight: 900; text-align: center; background-color: rgba(255, 255, 255, 0.8);\">冬奥会短道速滑智能化转播</span></p>\n<p><span style=\"font-size: 14pt;\"><span style=\"color: #222222; font-family: Consolas, \'Lucida Console\', \'Courier New\', monospace; white-space: pre-wrap; background-color: #ffffff;\">所属分类：书籍装帧 </span><span style=\"background-color: #ffffff; color: #222222; font-family: Consolas, \'Lucida Console\', \'Courier New\', monospace; white-space: pre-wrap;\">实验学时：6 </span><span style=\"background-color: #ffffff; color: #222222; font-family: Consolas, \'Lucida Console\', \'Courier New\', monospace; white-space: pre-wrap;\">实验操作步骤：24</span></span></p>\n<p><span style=\"font-size: 14pt;\"><span style=\"color: #222222; font-family: Consolas, \'Lucida Console\', \'Courier New\', monospace; white-space: pre-wrap; background-color: #ffffff;\">实验负责人：赵志勇 </span><span style=\"background-color: #ffffff; color: #222222; font-family: Consolas, \'Lucida Console\', \'Courier New\', monospace; white-space: pre-wrap;\">联系电话：15801930901</span></span></p>\n<p><span style=\"color: #222222; font-family: Consolas, \'Lucida Console\', \'Courier New\', monospace; font-size: 14pt; white-space: pre-wrap; background-color: #ffffff;\">实验简介：当前的书籍装帧设计教学多止步于印刷之前的数码打样，很难呈现承印纸张和印刷工艺选择后的真实效果。本实验辅助书籍装帧设计者在平面创意完成之后，通过上载平面设计效果图，完成开本大小、纸艺肌理、装订方式、常用工艺的形式选择，快速得到视觉创意、承印材质、印制工艺三者合成后的仿真效果，为设计者选择最终的装帧形式提供直观的审美体验。</span><span style=\"background-color: #ffffff; color: #222222; font-family: Consolas, \'Lucida Console\', \'Courier New\', monospace; font-size: 12px; white-space: pre-wrap;\"><br /></span></p>', '2020-12-12 15:14:13');
INSERT INTO `experimental_description` VALUES ('24', '考核要求', '<p><span style=\"color: #222222; font-family: Microsoft&nbsp;YaHei, Helvetica&nbsp;Neue, PingFang&nbsp;SC, sans-serif; font-size: 18pt; white-space: pre-wrap; background-color: #ffffff;\">教师根据设计主题和创意效果对学生生成的实验模型进行过程评价，按实验提供的承印材料和印刷工艺对平面作品的切合度进行质量评价。学生能够采用虚拟现实的方式进行书籍的线上快速设计、分享、评定。考核内容重点：纸张选用实验；装订形式实验；印制工艺实验，教师需据实验结果考核作品与内容选择的匹配度，重点评价学生对纸张之间的细微差别和印刷工艺特殊效果的观察能力和应用能力。</span></p>', '2020-12-12 20:12:56');
